import React, { useCallback, useState } from 'react';
import { actionCreators as cartActions } from '../redux/modules/cart';
import { useDispatch } from 'react-redux';

import styled from 'styled-components';
import Button from '../elements/Button';
import { priceUnit } from '../shared/common';

const Modal = ({ close, title, price, id, img }) => {
	const dispatch = useDispatch();

	const [quantity, setQuantity] = useState(1);
	const [currentPrice, setCurrrentPrice] = useState(Number(price));

	const modalClose = useCallback(() => {
		close();
	}, [close]);

	const onclickPlus = useCallback(() => {
		if (quantity > 99) return;
		setQuantity(quantity + 1);
		setCurrrentPrice(Number(price) * (quantity + 1));
	}, [quantity, price]);

	const onclickMinus = useCallback(() => {
		if (quantity < 2) return;
		setQuantity(quantity - 1);
		setCurrrentPrice(currentPrice - parseInt(price));
	}, [quantity, currentPrice, price]);

	const onClickAddCart = useCallback(() => {
		dispatch(cartActions.addCardMiddleWare(id, title, currentPrice, img, quantity));
		close();
	}, [dispatch, id, title, currentPrice, img, quantity]);

	return (
		<>
			<Wrapper>
				<Container>
					<TextLayout>
						<Subject>{title}</Subject>
						<Price>
							{priceUnit(price)}
							<Count className="count">
								<PlusMinusBtn onClick={onclickMinus}>-</PlusMinusBtn>
								<input readOnly value={quantity} />
								<PlusMinusBtn onClick={onclickPlus}>+</PlusMinusBtn>
							</Count>
						</Price>
					</TextLayout>
					<PriceLayout>
						<PriceContent>
							<Subject>합계</Subject>
							<Price>{priceUnit(currentPrice)}원</Price>
						</PriceContent>
						<PointInfo>
							<Point>적립</Point>로그인 후, 적립혜택 제공
						</PointInfo>
					</PriceLayout>
					<Buttons>
						<Button width={'47%'} bg={'white'} color={'black'} onClick={modalClose}>
							취소
						</Button>
						<Button width={'47%'} onClick={onClickAddCart}>
							장바구니 담기
						</Button>
					</Buttons>
				</Container>
			</Wrapper>
		</>
	);
};

const Wrapper = styled.div`
	position: fixed;
	display: flex;
	justify-content: center;
	align-items: center;
	width: 100vw;
	height: 100vh;
	background-color: rgba(44, 44, 44, 0.267);
	top: 0;
	left: 0;
	z-index: 999;
`;

const Container = styled.div`
	display: flex;
	flex-direction: column;
	align-items: center;
	width: 400px;
	height: 270px;
	background-color: white;
	border-radius: 1em;
	padding: 30px;
`;

const TextLayout = styled.div`
	width: 100%;
	padding: 0.5em;
	p {
		margin: 0.5em;
	}
`;

const Subject = styled.span`
	font-size: 1em;
	font-weight: normal;
`;

const Price = styled.span`
	font-size: 1.2em;
	font-weight: bold;
	display: flex;
	justify-content: space-between;
`;

const PriceLayout = styled.div`
	width: 100%;
	height: 90px;
	margin: 20px 0;
`;

const PriceContent = styled.div`
	display: flex;
	flex-direction: row;
	justify-content: space-between;
`;

const PointInfo = styled.div`
	display: flex;
	flex-direction: row;
	justify-content: end;
	font-size: 0.8em;
`;

const Point = styled.div`
	margin: 0 10px;
	padding: 0 5px;
	font-weight: 100;
	background-color: orange;
	color: white;
	border-radius: 10px;
`;

const Buttons = styled.div`
	display: flex;
	flex-direction: row;
	justify-content: space-around;
	width: 100%;
`;

const Count = styled.div`
	display: flex;
	border: 1px solid rgba(44, 44, 44, 0.267);
	height: 27px;
	input {
		text-align: center;
		width: 27px;
		height: 25px;
		border: none;
		pointer-events: none;
	}
`;

const PlusMinusBtn = styled.button`
	border: none;
	height: 27px;
	width: 27px;
`;

export default Modal;

