import React, { useEffect } from 'react';
import styled from 'styled-components';
import { Button, Grid, Text } from '../elements';
import CartCard from '../components/CartCard';
import { useSelector, useDispatch } from 'react-redux';
import { actionCreators as cartActions } from '../redux/modules/cart';
import { history } from '../redux/configureStore';
import { priceUnit } from '../shared/common';
import Post from '../components/Post'
const Cart = props => {
	const dispatch = useDispatch();
	const user_info = useSelector(state => state.user.user);
	const cart_list = useSelector(state => state.cart.cart_list);

	const [address, setAddress] = React.useState("");
	const [popup, setPopup] = React.useState(false);

	console.log(cart_list);
	// 장바구니에 담긴 상품의 총 금액.
	let total_price = cart_list.map(c => parseInt(c.price.replace(/,/g, "")) * c.quantity).reduce((acc, curr) => acc + curr, 0);
	//기본 배송비 상품 담았을 때, 안 담았을 때.
	const delivery_charge = cart_list.length === 0 ? 0 : 3000;
	useEffect(() => {
		dispatch(cartActions.getCartAPI());
	}, [user_info, dispatch]);

	console.log('cart_list');
	console.log(cart_list);

	return (
		<Grid margin-left="0px 0px 50px 0px">
			<All>
				<TitleBox>
					<Title>장바구니</Title>
					<DeleteUl>
						<li>전체상품 {cart_list.length}개</li>
					</DeleteUl>
				</TitleBox>
				<ContentBox>
					<CartBox>
						{cart_list.length === 0 && <NoneCart>장바구니에 담긴 상품이 없습니다</NoneCart>}
						<CartCard />
					</CartBox>
					<div>
						<OrderBox>
							<AddressBox>
								<AdressIcon src="https://res.kurly.com/pc/service/cart/2007/ico_location.svg" />
								<span className="address">배송지</span>
								<Text margin="7px">{user_info?.address}</Text>
								<Text size="14px" color="#5f0080">
									샛별배송
								</Text>
								<Text size="16px" weight="700" color="#4c4c4c">{setAddress}</Text>
								<InputBox  onClick={()=>{
											setPopup(!popup)
											}}
											>🔍︎ 주소 검색
									{/* <span>주소 검색</span> */}

											{
										popup && 
											<Post address={address} setAddress={setAddress}></Post>
											} 
								</InputBox>
							</AddressBox>
							<PriceBox>
								<ProductPriceBox>
									<Text>상품금액</Text>
									<Text>{priceUnit(total_price)} 원</Text>
								</ProductPriceBox>
								<ProductPriceBox>
									<Text>배송비</Text>
									<Text>+{priceUnit(delivery_charge)} 원</Text>
								</ProductPriceBox>
								<ProductPriceBox>
									<Text>결제예정금액</Text>
									<Text>
										<TotalPrice>{total_price + delivery_charge}</TotalPrice> 원
									</Text>
								</ProductPriceBox>
								<SaveMoneyBox>
									<SaveMoney>적립</SaveMoney>
									<MoneyInfo>구매 시 0원 적립</MoneyInfo>
								</SaveMoneyBox>
							</PriceBox>
						</OrderBox>
						<OrderInfoBox>
							<Button
								margin="25px 0px"
								_onClick={() => {
									alert('주문이 완료되었습니다.');
									history.push('/');
								}}
							>
								주문하기
							</Button>
							<Info>
								<li>· '입금확인' 상태일 때는 주문 내역 상세에서 직접 주문취</li>
								<li>소가 가능합니다.</li>
								<li>· '입금확인' 이후 상태에는 고객센터로 문의해주세요.</li>
							</Info>
						</OrderInfoBox>
					</div>
				</ContentBox>
			</All>
		</Grid>
	);
};

export default Cart;

const All = styled.div`
	display: flex;
	justify-content: center;
	flex-direction: column;
	align-items: center;
`;

const NoneCart = styled.p`
	margin-top: 150px;
`;

const OrderInfoBox = styled.div`
	width: 285px;
	position: relative;
	left: 12px;
`;

const SaveMoneyBox = styled.div`
	text-align: right;
	padding: 0px 18px 0px 0px;
	margin-top: 10px;
`;

const SaveMoney = styled.span`
	font-size: 8px;
	display: inline-block;
	padding: 1px 8px;
	color: #ffffff;
	font-weight: 500;
	border-radius: 12px;
	border: 1px solid #f7965f;
	text-align: center;
	background-color: #ffbf00;
	vertical-align: middle;
	position: relative;
	right: 5px;
	top: 1px;
`;

const MoneyInfo = styled.span`
	font-size: 12px;
	color: #666666;
`;

const DeleteUl = styled.ul`
	display: flex;
	font-size: 14px;
	position: relative;
	left: -30px;
	top: 60px;
	gap: 15px;
`;

const Info = styled.ul`
	font-size: 12px;
	margin: 0px;
	padding: 0px;
	color: #666666;
	& li:nth-child(2) {
		padding-left: 12px;
	}
	& li:nth-child(3) {
		margin-top: 10px;
	}
`;

const ProductPriceBox = styled.div`
	display: flex;
	height: 35px;
	justify-content: space-between;
	padding: 0px 20px;
	font-size: 16px;
	color: #4c4c4c;
`;

const Title = styled.div`
	text-align: center;
	font-size: 30px;
	font-weight: 600;
`;

const TitleBox = styled.div`
	padding: 55px 55px 55px 0px;
`;
const CartBox = styled.div`
	width: 742px;
	border-top: 1px solid #333;
	padding-left: 9px;
	line-height: 20px;
	box-sizing: content-box;
	margin: 10px;
	font-size: 14px;
	text-align: center;
	font-weight: 500;
`;

const AdressIcon = styled.img`
	height: 20px;
	position: relative;
	top: 5px;
`;

const AddressBox = styled.div`
	border-bottom: 1px solid #f2f2f2;
	padding: 20px;
	box-sizing: border-box;
	font-size: 16px;
	font-weight: 500;
	& p {
		margin: 7px 0px 0px 0px;
	}
`;

const ContentBox = styled.div`
	display: flex;
`;

const InputBox = styled.div`
	border: 1px solid purple;
	margin: 17px 0px 0px;
	border-radius: 5px;
	height: 36px;
