import React from 'react';
import styled from 'styled-components';
import Card from '../elements/Card';

const Container = styled.div`
	display: flex;
	flex-direction: column;
	align-items: center;
	justify-content: center;
	font-size: 2em;
	width: 100%;
	height: 660px;
	background-color: rgb(250, 245, 255);
	padding: 10px;
`;

const Contents = styled.div`
	display: flex;
	justify-content: center;
	flex-direction: row;
	margin-right: 20px;
	/* img {
		width: 330px;
		margin: 10px;
	} */
`;

const Bargain = () => {
	return (
		<Container>
			<p style={{ fontWeight: '400', fontSize: '30px' }}>특가/혜택</p>
			<Contents>
				<Card
					width="330px"
					height="450px"
					margin="0 5px"
					title="금주의 과일 최대 20% 할인"
					price="가을 하늘 아래 영글은"
					titleSize="18px"
					priceSize="15px"
					center={true}
					color="lightgray"
					img="https://firebasestorage.googleapis.com/v0/b/kurly-8cf7b.appspot.com/o/bargain1.jpg?alt=media&token=c750e177-20ba-4835-8d94-6e5d0dc04956"
				/>
				<Card
					width="330px"
					height="450px"
					margin="0 5px"
					title="뷰티 소품 최대 40% 할인"
					price="특가로 만나는 필수 아이템"
					titleSize="18px"
					priceSize="15px"
					center={true}
					color="lightgray"
					img="https://firebasestorage.googleapis.com/v0/b/kurly-8cf7b.appspot.com/o/bargain2.jpg?alt=media&token=310b82fe-1397-48a5-a1e9-e95ecbc77a8a"
				/>
				<Card
					width="330px"
					height="450px"
					margin="0 5px"
					title="최대 15% 할인 + 25% 추가 쿠폰"
					price="아모레퍼시픽, 단 48시간의 혜택"
					titleSize="18px"
					priceSize="15px"
					center={true}
					color="lightgray"
					img="https://firebasestorage.googleapis.com/v0/b/kurly-8cf7b.appspot.com/o/bargain3.jpg?alt=media&token=70af4dd5-def2-4a4b-b4a6-bc560e05fb3e"
				/>
			</Contents>
		</Container>
	);
};

export default Bargain;
